"use strict";

/* 
 * layouter for arrow
 */

var m_map = require('map.js');
var m_arrow = require('node/arrow.js');
var m_arrowWidget = require('widget/arrowWidget.js');
var m_nodeLayouter = require('layouter/nodeLayouter.js');
var m_textNode = require('node/textNode.js');
var m_trig = require('trigonometry.js');

function ArrowLayouter(/* m_arrowWidget.ArrowWidget */ p_widget) {
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT { */
            var LEN = 1; if ( arguments.length !== LEN ) { throw new Error('arguments length is ' + arguments.length + ', should be ' + LEN); }
            var PARAM = p_widget; if ( !(PARAM instanceof m_arrowWidget.ArrowWidget) ) { throw new Error('type error: param p_widget should be of type m_arrowWidget.ArrowWidget'); }
        /* @DEVELOPMENT } */
        m_nodeLayouter.NodeLayouter.call(this, p_widget);
        this._seenNodes = [];
    }
}

function arrowLayouter(/* m_arrowWidget.ArrowWidget */ p_widget) {
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT { */
            var LEN = 1; if ( arguments.length !== LEN ) { throw new Error('arguments length is ' + arguments.length + ', should be ' + LEN); }
            var PARAM = p_widget; if ( !(PARAM instanceof m_arrowWidget.ArrowWidget) ) { throw new Error('type error: param p_widget should be of type m_arrowWidget.ArrowWidget'); }
        /* @DEVELOPMENT } */
        return new ArrowLayouter(p_widget);
    } else {
        return new ArrowLayouter();
    }
}


ArrowLayouter.prototype = new m_nodeLayouter.NodeLayouter();
ArrowLayouter.prototype.constructor = ArrowLayouter;

ArrowLayouter.prototype.layout = function() {
    var bBox1 = this._node.map().painter(this._node.sourceNode()).bBox();
    var sourceCenter = m_trig.centerOfRectangle(bBox1.x, bBox1.y, bBox1.x2, bBox1.y2);
    var bBox2 = this._node.map().painter(this._node.targetNode()).bBox();
    var targetCenter = m_trig.centerOfRectangle(bBox2.x, bBox2.y, bBox2.x2, bBox2.y2);

    var widget = this._node.map().widget(this._node);

    widget.x1(sourceCenter.x);
    widget.y1(sourceCenter.y);
    widget.x2(targetCenter.x);
    widget.y2(targetCenter.y);
};

exports.ArrowLayouter = ArrowLayouter;
exports.arrowLayouter = arrowLayouter;
