"use strict";

var $ = require('jquery');
var Raphael = require('raphael');
var _ = require('underscore');
var m_map = require('map.js');
var m_ui = require('ui/UI.js');
var TEST = true;

$(document).ready(function() {
    var map = m_map.map();
    window.mapitor = {
        map: map,
        ui: m_ui.ui(document.getElementById('mapparent'), map)
    };

    // map.resize(window.mapitor.ui.domWidth(), window.mapitor.ui.domHeight());
    map.resize(400, 400);

    if ( TEST ) {
        window.mapitor.ui.doTest();
    } else {
        window.mapitor.ui.paint();
    }
});
