"use strict";

var m_node = require('node/node.js');
var m_area = require('node/area.js');
var m_layouterFactory = require('layouter/layouterFactory.js');

var LayoutMethod = { basic: 'basic', circular: 'circular', hierarchical: 'hierarchical' };

function Map() {
    /* @DEVELOPMENT { */
         var LEN = 0; if ( arguments.length !== LEN ) { throw new Error('arguments length is ' + arguments.length + ', should be ' + LEN); }
    /* @DEVELOPMENT } */
    this._width = 256;
    this._height = 256;
    this._painters = {}; // map<node-id, painter>
    this._layouters = {}; // map<node-id, layouter>
    this._area = m_area.area(this, null, { x: 2, y: 2, width: this._width - 4, height: this._height - 4} );
    this._layoutMethod = LayoutMethod.basic;
    this._currentNode = null;
}

function map() {
    /* @DEVELOPMENT { */
         var LEN = 0; if ( arguments.length !== LEN ) { throw new Error('arguments length is ' + arguments.length + ', should be ' + LEN); }
    /* @DEVELOPMENT } */
    return new Map();
}


Map.prototype.constructor = Map;

/* m_area.Area */ Map.prototype.area = function() {
    return this._area;
}

/* m_node.Node */ Map.prototype.currentNode = function(/* m_node.Node */ p_node) {
    if ( typeof(p_node) !== 'undefined' ) {
        this._currentNode = p_node;
        return this;
    }
    return this._currentNode;
}


/* this */ Map.prototype.addPainter = function(/* Painter */ p_painter) {
    this._painters[p_painter.node().id()] = p_painter;
    return this;
};

/* Painter|undefined */ Map.prototype.painter = function(/* m_node.Node */ p_node) {
    return this._painters[p_node.id()];
};

/* LayoutMethod|undefined */ Map.prototype.layoutMethod = function(/* optional LayoutMethod */ p_layoutMethod) {
    if ( typeof(p_layoutMethod) !== 'undefined' ) {
        this._layoutMethod = p_layoutMethod;
        return this;
    }
    return this._layoutMethod;
};

/* this */ Map.prototype.layout = function() {
    var layouter = m_layouterFactory.layouterFactory(this).forObject(this._area);
    layouter.layout();

    return this;
};

/* this */ Map.prototype.addLayouter = function(/* Layouter */ p_layouter, /* LayoutMethod */ p_method) {
    if ( typeof(this._layouters[p_layouter.node().id()]) === 'undefined' ) {
        this._layouters[p_layouter.node().id()] = {};
    }

    this._layouters[p_layouter.node().id()][p_method] = p_layouter;
    return this;
};

/* Layouter|undefined */ Map.prototype.layouter = function(/* m_node.Node */ p_node, /* LayoutMethod */ p_method) {
    if ( typeof(this._layouters[p_node.id()]) === 'undefined' ) {
        return undefined;
    }
    return this._layouters[p_node.id()][p_method];
};

/* this */ Map.prototype.resize = function(/* int */ p_width, /* int */ p_height) {
    this._width  = p_width;
    this._height = p_height;
    this._area.resize(p_width, p_height);
    return this;
}

exports.Map = Map;
exports.map = map;
exports.LayoutMethod = LayoutMethod;
