/*
 * this will be able to paint a text node on a paper
 */

"use strict";

var m_map = require('map.js');
var m_nodePainter = require('painter/nodePainter.js');
var m_textNode = require('node/textNode.js');
var md5 = require('md5');

function TextNodePainter(/* m_textNode.TextNode */ p_node, /* Raphael */ p_paper) {
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT { */
            var LEN = 2; if ( arguments.length !== LEN ) { throw new Error('arguments length is ' + arguments.length + ', should be ' + LEN); }
            var PARAM = p_node; if ( !(PARAM instanceof m_textNode.TextNode) ) { throw new Error('type error: param p_map should be of type m_map.Map'); }
        /* @DEVELOPMENT } */

        m_nodePainter.NodePainter.call(this, p_node, p_paper);
        this._textAttr = { 
            'stroke': '#000', 
            'fill': '#000', 
            'font-family': 'courier', 
            'font-size': '12', 
            'text-anchor': 'middle' 
        };
        this._rectAttr = { 
            'stroke': '#999', 
        };
    }
}

function textNodePainter(/* m_textNode.TextNode */ p_node, /* Raphael */ p_paper) {
    if ( arguments.length > 0 ) {
        /* @DEVELOPMENT { */
            var LEN = 2; if ( arguments.length !== LEN ) { throw new Error('arguments length is ' + arguments.length + ', should be ' + LEN); }
            var PARAM = p_node; if ( !(PARAM instanceof m_textNode.TextNode) ) { throw new Error('type error: param p_node should be of type m_textNode.TextNode'); }
        /* @DEVELOPMENT } */

        return new TextNodePainter(p_node, p_paper);
    } else {
        return new TextNodePainter();
    }
}


TextNodePainter.prototype = new m_nodePainter.NodePainter();
TextNodePainter.prototype.constructor = TextNodePainter;

TextNodePainter.prototype.paint = function() {
    var bbox = null;
    if ( this._t === undefined ) {
        this._t = this._paper.text(this._node.x() + 2, this._node.y() + 2, this._node.text())
        this._t.attr(this._textAttr);
        bbox = this._t.getBBox();
        this._rect = this._paper.rect(this._node.x() - bbox.width / 2, this._node.y() - bbox.height / 2, bbox.width + 4, bbox.height + 4, 3)
        var self = this;
        this._t.click(function() {
            window.mapitor.ui.editTextNode(self._node);
        });
    } else {
        this._t.attr('text', this._node.text());
        this._t.attr(this._textAttr);
        this._t.attr('x', this._node.x() + 2);
        this._t.attr('y', this._node.y() + 2);
        bbox = this._t.getBBox();
    }
    this._rect.attr(this._rectAttr);
    this._rect.attr( { 'x' : this._node.x() - bbox.width / 2, 'y' : this._node.y() - bbox.height / 2, 'width' : bbox.width + 4, 'height' : bbox.height + 4 } );
    if ( this._node === this._node.map().focusedNode() ) {
        this._rect.attr('stroke-width', '3').attr('stroke', '#000');
    }
    this._t.toFront();
};

TextNodePainter.prototype.bBox = function() {
    if ( this._bboxCacheHash === this._node.hashCode() ) {
        return this._bboxCache;
    }

    this.paint();
    var bbox = this._rect.getBBox();
    this._bboxCacheHash = this._node.hashCode();
    this._bboxCache = bbox;
    return bbox;
};

exports.TextNodePainter = TextNodePainter;
exports.textNodePainter = textNodePainter;
